INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001181, N'أطقم الاتصالات اللاسلكية / سماعات الرأس', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كقطعة من المعدات المصممة للسماح للفرد باستخدام الهاتف أو أي جهاز اتصال صوتي آخر دون الحاجة إلى تثبيته على الأذن ، إما عن طريق استخدام ميكروفون مستقل أو ميكروفون مدمج في سماعات الرأس. تتضمن منتجات مثل مجموعات الهاتف المحمول بدون استخدام اليدين.', N'يستثني على وجه التحديد سماعات الرأس المستخدمة فقط للاستماع وليس للاتصال ثنائي الاتجاه. باستثناء المنتجات مثل حاملات هواتف السيارة.')
