INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001183, N'حراس إشعاع الهاتف المحمول', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مصمم خصيصًا لتقليل تعرض المستخدم للإشعاع الذي قد ينبعثه أو يتلقاها الهاتف المحمول بشكل كبير.', N'يستثنى من ذلك منتجات مثل واقيات إشعاع الكمبيوتر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001125, N'معالجات الكمبيوتر', N'يشمل أي منتجات موصوفة / تمت ملاحظتها على أنها معالجات تستخدم لتفسير الأوامر.', N'باستثناء منتجات مثل ذاكرة الكمبيوتر والأجهزة الطرفية.')
