INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001163, N'بوابات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يمكّن نظامين مختلفين لهما وظائف متشابهة من التواصل مع بعضهما البعض.', N'باستثناء منتجات مثل المحولات والمحاور وأجهزة التوجيه وجدران الحماية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001169, N'مفاتيح الشبكة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز شبكات كمبيوتر يقوم بتصفية المعلومات / البيانات بين أجزاء الشبكة.', N'باستثناء المنتجات مثل الموزعات وأجهزة التوجيه والبوابات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001177, N'حاملي الهاتف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مخصص لتثبيت الهاتف المحمول في مكانه. تشمل منتجات مثل حاملات هواتف السيارة ومشابك حزام الهاتف المحمول.', N'يُستثنى من ذلك المنتجات مثل حافظات الهواتف المحمولة ولفافات الهواتف المحمولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005680, N'مناديل الهاتف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مناديل مصممة خصيصًا لتنظيف الهواتف الثابتة والجوالة. يشمل المنتجات التي يتم صرفها من الأحواض أو الأكياس وقد تحتوي على مبيد للجراثيم لقتل الجراثيم الشائعة.', N'باستثناء منتجات مثل مناديل تنظيف الكمبيوتر ومناديل التنظيف السمعية والبصرية ومناديل التنظيف المنزلية للأغراض العامة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001382, N'حزم الاتصالات المتنوعة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمنتجات اتصالات مميزة أو أكثر يتم بيعها معًا والموجودة في المخطط ولكنها تنتمي إلى فئات مختلفة ، أي منتجين أو أكثر متضمنين في نفس الحزمة التي تتقاطع مع الفئات داخل عائلة الاتصالات. تتضمن منتجات مثل الهاتف المحمول الذي يتم بيعه مع مجموعة أدوات التحدث الحر. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثني منتجات مثل المنتجات من قطاع الاتصالات التي تُباع مع منتجات من قسم آخر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001184, N'آلات الرد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز إلكتروني ثابت يقوم ، في حالة عدم وجود الطرف المتصل به ، بالرد تلقائيًا على المكالمات الواردة برسالة مسجلة مسبقًا ويسجل الرسائل من المتصلين لتشغيلها لاحقًا. تشمل منتجات مثل أجهزة الرد الرقمي وأجهزة الرد على الأشرطة.', N'باستثناء منتجات مثل الأجهزة متعددة الوظائف التي تشتمل على جهاز الرد على المكالمات والتي تم تصنيفها مع الآلات المكتبية ، بالإضافة إلى الهواتف والهواتف المزودة بجهاز رد داخلي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001185, N'نظم المؤتمرات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها وسيلة يتم من خلالها توصيل الهواتف للسماح للأشخاص في مواقع مختلفة بالتحدث إلى بعضهم البعض والاستماع إليهم في وقت واحد. قد تتضمن هذه الأنظمة أيضًا شاشة مرئية صغيرة.', N'باستثناء منتجات مثل الاتصال الداخلي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005677, N'مستهلكات آلة الفاكس', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مصممة خصيصًا لاستبدال / إعادة تعبئة مكونات جهاز الفاكس بشكل دوري التي تم استنفادها أو تلفها بسبب الاستخدام ، مثل رقائق الحبر. تشمل المنتجات خراطيش الأسطوانة وخراطيش الأفلام والأطقم التي تحتوي على عناصر بديلة متعددة مثل الحبر والأسطوانة ووحدة التصوير.', N'يستثنى من ذلك منتجات مثل أجهزة الفاكس والطابعة والمواد الاستهلاكية لكاتب النوع.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001186, N'أجهزة الفاكس', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز إلكتروني ثابت يمكنه إرسال أو استقبال نسخ من الصور والنصوص والصور عبر خط الهاتف.', N'باستثناء منتجات مثل الأجهزة متعددة الوظائف التي تشتمل على جهاز فاكس والتي تم تصنيفها مع آلات مكتبية ، بالإضافة إلى الهواتف أو أجهزة الرد التي تُباع منفردة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005681, N'ملحقات أجهزة الاتصالات الثابتة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كملحق لجهاز اتصال ثابت غير مدمج في وحدة بيع جهاز الاتصال الرئيسي ولكن يتم بيعه بشكل منفصل. يتضمن منتجات مثل رنين جرس الهاتف لتمكين الفرد من سماع هاتف في غرفة أخرى وأجهزة مشاركة الخط للهواتف.', N'يستثنى من ذلك المنتجات مثل المواد الاستهلاكية لجهاز الفاكس التي يلزم استبدالها على أساس دوري.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001383, N'أجهزة اتصالات ثابتة أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز اتصال ثابت ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات الموجودة في الطوب الموجود داخل المخطط.', N'يستثنى من ذلك جميع أجهزة الاتصالات الثابتة المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001384, N'حزم متنوعة لأجهزة الاتصالات الثابتة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجين أو أكثر من منتجات أجهزة الاتصالات الثابتة التي يتم بيعها معًا ، والتي توجد ضمن المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر موجودان في نفس الحزمة التي تتقاطع مع الطوب ضمن فئة أجهزة الاتصالات الثابتة. يشمل منتجات مثل الهاتف الذي يُباع بجهاز رد منفصل. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثني منتجات مثل الهاتف الذي يتم بيعه مع عرض معرف المتصل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001188, N'قسائم الدفع المسبق للاتصالات الثابتة / بطاقات الاتصال', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كبطاقة / قسيمة يتم شراؤها من بائع تجزئة ، مما يسمح للمشتري بإجراء مكالمات هاتفية من أجهزة الاتصال الثابتة حتى قيمة البطاقة أو الإيصال.', N'باستثناء منتجات مثل بطاقات / بطاقات الدفع المسبق للهاتف المحمول.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001189, N'انتركم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كنظام ثابت يتألف من سلسلة من مجموعات قواعد الاتصالات الإلكترونية المتصلة ومجموعات اليد التي يمكن استخدامها للتواصل بين المواقع المختلفة.', N'باستثناء المنتجات مثل الهواتف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001190, N'مقسمات الهاتف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يحتوي على مفاتيح يمكن بواسطتها إجراء اتصال من خط هاتف وارد إلى أي سلسلة من سلسلة الخطوط الصادرة. يمكن أن تكون المفاتيح إما ميكانيكية أو إلكترونية مع إجراء التحويل تلقائيًا ، ويتم التحكم فيه بواسطة إشارات في المكالمة الواردة أو يتم التحكم فيها يدويًا بواسطة عامل.', N'باستثناء المنتجات مثل الهواتف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001191, N'الهواتف', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز إلكتروني ثابت يحول الصوت إلى إشارات كهربائية ليتم نقلها عبر مسافات ثم يحول الإشارات المستلمة مرة أخرى إلى أصوات. يجب توصيل هذه المنتجات بشبكات الأسلاك الهاتفية من خلال نقطة اتصال ، والتي عادة ما تكون مثبتة على الحائط. تشمل منتجات مثل الهواتف اللاسلكية والهواتف السلكية بالإضافة إلى الهواتف المزودة بجهاز رد مدمج.', N'يستثنى من ذلك منتجات مثل الأجهزة متعددة الوظائف التي تشتمل على هاتف والتي تم تصنيفها مع آلات المكتب ، بالإضافة إلى الهواتف المحمولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001192, N'مجموعات راديو الاتصالات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمستقبل إلكتروني يكتشف ويزيل تشكيل وتضخيم الإشارات الكهرومغناطيسية المرسلة عبر مسافات شاسعة ، على سبيل المثال عبر بلد ما وحول العالم وفي الفضاء. عادةً ما تغطي الاتصالات نطاقًا تردديًا واسعًا ويمكن أن تكون شفرة مورس وراديو وبيانات وإشارات صوتية ، بما في ذلك جميع المنتجات (على سبيل المثال العسكرية أو خدمة الطوارئ أو المواصفات الصناعية أو غيرها من المواصفات الخاصة) التي تناسب تعريف المنتج ، بما في ذلك المنتجات مثل الهواة (هام) مجموعات الراديو وأجهزة الراديو البحرية.', N'باستثناء منتجات مثل Two-Way Radios.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001193, N'معدات GPS - اتصالات متنقلة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من الأجهزة المستخدمة في نظام تحديد المواقع العالمي ، والذي يستخدم الأقمار الصناعية وأجهزة الاستقبال والبرامج للسماح للمستخدمين بتحديد موقعهم الجغرافي الدقيق. يشمل جميع المنتجات (على سبيل المثال العسكرية ، خدمة الطوارئ ، الصناعية أو المواصفات الخاصة الأخرى) التي تناسب تعريف المنتج.', N'باستثناء منتجات مثل برنامج GPS وأنظمة الملاحة GPS الخاصة بالسيارات والهواتف المحمولة المزودة بنظام GPS مدمج.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001194, N'برنامج GPS - اتصالات المحمول', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من البرامج أو البيانات المستخدمة في نظام تحديد المواقع العالمي ، والذي يستخدم الأقمار الصناعية وأجهزة الاستقبال والبرامج للسماح للمستخدمين بتحديد موقعهم الجغرافي الدقيق. يتضمن برنامج GPS وخرائط للجهاز البحري أو التلقائي أو جهاز GPS شخصي.', N'باستثناء منتجات مثل معدات GPS. يستثني أيضًا منتجات مثل معدات GPS وبرامج / خرائط GPS التي لا تأتي على قرص مضغوط أو أي جهاز آخر مسجل مسبقًا مثل المحتوى القابل للتنزيل.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006237, N'برنامج GPS - اتصالات متنقلة - رقمية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أي من البرامج أو البيانات التي يتم تنزيلها أو بثها ، والمستخدمة في جهاز نظام تحديد المواقع العالمي ، والذي يستخدم الأقمار الصناعية وأجهزة الاستقبال والبرامج للسماح للمستخدمين بتحديد موقعهم الجغرافي الدقيق. يتضمن برنامج GPS وخرائط للجهاز البحري أو التلقائي أو جهاز GPS شخصي.', N'باستثناء منتجات مثل معدات GPS وبرامج / خرائط GPS التي تأتي على قرص مضغوط أو أي جهاز آخر مسجل مسبقًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003779, N'أجهزة / خدمات الاتصالات المتنقلة - قطع غيار', N' يشمل أي منتجات قد يتم وصفها / ملاحظتها على أنها قطع غيار لأجهزة وخدمات الاتصالات المتنقلة ، بما في ذلك المنتجات مثل لوحات المفاتيح وشاشات LCD للهواتف المحمولة. يشمل جميع المنتجات (على سبيل المثال العسكرية ، خدمة الطوارئ ، الصناعية أو المواصفات الخاصة الأخرى) التي تناسب تعريف المنتج.', N'يستثني على وجه التحديد لفائف الهاتف المحمول. يستثنى من ذلك منتجات مثل برامج الهواتف المحمولة ووسائل الوقاية من الإشعاع وحالات الاتصالات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001385, N'أجهزة / خدمات اتصالات متنقلة أخرى', N'يشمل أي منتجات قد يتم وصفها / ملاحظتها على أنها منتجات خدمات وأجهزة الاتصالات المتنقلة ، حيث لا يتمكن مستخدم المخطط من تصنيف المنتجات في قوالب موجودة داخل المخطط.', N'يستثني جميع منتجات خدمات وخدمات الاتصالات المتنقلة المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001386, N'حزم متنوعة لأجهزة / خدمات الاتصالات المتنقلة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجان أو أكثر من منتجات وخدمات الاتصالات المتنقلة المميزة التي يتم بيعها معًا ، والتي توجد ضمن المخطط الذي ينتمي إلى قوالب مختلفة ولكن لنفس الفئة ، أي منتجين أو أكثر متضمن في نفس الحزمة عبر الطوب داخل فئة أجهزة وخدمات الاتصالات المتنقلة. تشمل منتجات مثل الهواتف المحمولة وبطاقات SIM التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يُستثنى من ذلك منتجات مثل الهواتف المحمولة ومجموعات أدوات التحدث الحر التي تُباع معًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001196, N'بطاقات SIM للهاتف المحمول / محولات بطاقة SIM', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كوحدة هوية للمشترك (SIM) على بطاقة قابلة للإزالة يمكن إدخالها في أي هاتف محمول يعمل بنظام GSM ، والتي تخزن بشكل آمن مفتاح مشترك الخدمة (IMSI) المستخدم لتعريف المشترك على أجهزة الهاتف المحمول (مثل أجهزة الكمبيوتر والهواتف المحمولة). تحتوي لوحة الدوائر المطبوعة الصغيرة أيضًا على ذاكرة لدليل شخصي للأرقام. يجمع محول بطاقة SIM بين بطاقات SIM من مناطق أو دول مختلفة أو أرقام شبكات مختلفة في بطاقة SIM واحدة فقط.', N'باستثناء منتجات مثل برامج الهاتف المحمول.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001197, N'برامج الهاتف المحمول', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمجموعة من البرامج أو البيانات المصممة خصيصًا لتشغيل الهاتف المحمول. تشمل منتجات مثل برامج الويب وسوق الأوراق المالية وبرامج البث والألعاب وبرامج نغمات الرنين.', N'باستثناء منتجات مثل بطاقات SIM وبرامج الكمبيوتر وبرامج GPS. يُستثنى من ذلك المنتجات التي يتم تنزيلها أو دفقها إلى العملاء.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006238, N'برامج الهاتف المحمول - رقمية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمجموعة من البرامج أو البيانات المصممة خصيصًا لتشغيل الهاتف المحمول والتي يتم تنزيلها أو دفقها إلى الهاتف. تشمل منتجات مثل برامج الويب وسوق الأوراق المالية وبرامج البث والألعاب وبرامج نغمات الرنين.', N'باستثناء منتجات مثل بطاقات SIM وبرامج الكمبيوتر وبرامج GPS.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007020, N'ملحقات الهاتف المحمول / الهاتف الذكي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملحقات للهواتف المحمولة / الهواتف الذكية. على سبيل المثال ، بوق منفصل لإرفاقه بالهاتف (يجب عدم الخلط بينه وبين مجموعة أدوات التحدث الحر).', N'باستثناء حالات الهاتف المحمول ، ومجموعات سماعات / سماعات الرأس ، وأجهزة حماية الهاتف المحمول من الإشعاع.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001198, N'الهواتف المحمولة / الهواتف الذكية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز لاسلكي محمول باليد يحول الصوت إلى إشارات كهربائية ليتم نقلها عبر مسافات ثم يحول الإشارات المستلمة مرة أخرى إلى أصوات. لا يلزم توصيل هذه الأجهزة بنقطة اتصال هاتف. تشمل الهواتف المحمولة بميزات مثل الشاشات الملونة ورسائل الفيديو ورسائل الصور والنطاق المزدوج وثلاثي الموجات بالإضافة إلى الهواتف الذكية التي يمكن أن توفر وظائف متقدمة مثل البريد الإلكتروني والمنظم الشخصي.', N'باستثناء المنتجات مثل الهواتف اللاسلكية وأجهزة الاستدعاء.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001124, N'مزودات طاقة الكمبيوتر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يوفر الطاقة في جميع أنحاء الكمبيوتر.', N'يستثني أي مصادر طاقة أخرى غير مصممة خصيصًا لأجهزة الكمبيوتر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001112, N'أدوات الكمبيوتر / أطقم الأدوات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمجموعة من الأدوات المصممة خصيصًا للاستخدام مع أجهزة الكمبيوتر.', N'باستثناء منتجات مثل "مجموعة أدوات DIY".')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001114, N'مساعد البيانات الشخصية / قلم المنظم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة مدببة تستخدم كأداة إدخال على شاشة حساسة للضغط.', N'باستثناء المنتجات مثل أقلام القرطاسية وأقلام الرصاص.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001199, N'أجهزة النداء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يحتوي على مستقبل راديو رقمي ، يتم ضبطه بشكل دائم على تردد واحد. يحتوي كل جهاز على رمز هوية فريد ، لذلك عندما يتم إرسال الرمز على تردد الراديو الصحيح ، يتعرف الجهاز عليه ويمكن أن يصدر صوتًا و / أو يعرض الرسائل الرقمية أو الأبجدية الرقمية.', N'باستثناء منتجات مثل الهواتف المحمولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005711, N'أجهزة البث / أجهزة التتبع الرقمية الشخصية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز إبلاغ إلكتروني يمكنه تتبع سوق الأوراق المالية رقميًا والتنبؤ بالطقس وعرض الأخبار الرئيسية وقد يعمل أيضًا كتقويم. يشمل المنتجات التي تقف بمفردها على منضدة أو سطح مكتب بالإضافة إلى الإصدارات ذاتية اللصق التي يمكن وضعها على الحائط.', N'باستثناء منتجات مثل برامج الهاتف المحمول ومنتجات GPS والساعات التي تتضمن ميزات البث.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001200, N'أجهزة راديو ثنائية الاتجاه', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز محمول ثنائي الاتجاه للتواصل عن طريق الموجات الكهرومغناطيسية ، بدون سلك توصيل. يمكن لهذه المنتجات فقط إرسال واستقبال الرسائل الشفوية عبر نطاق محدود من ترددات الراديو. تشمل منتجات مثل Walkie-Talkies.', N'يستثنى على وجه التحديد مجموعات راديو الهواة (هام). يُستثنى من ذلك منتجات مثل أجهزة الراديو ذات الاتجاه الواحد والهواتف المحمولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006227, N'اللافتة - جزء / ملحق بديل', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء بديل أو ملحق لمنتجات Sign.', N'يستثنى من ذلك منتجات مثل المقابس وجميع منتجات التصوير الفوتوغرافي / معدات الغرفة المظلمة الأخرى التي يتم تقديمها حاليًا في فئة طباعة التصوير الفوتوغرافي / معدات الغرفة المظلمة. يستثني على وجه التحديد منتجات مثل اللافتات المطبوعة مسبقًا واللافتات غير المطبوعة والعلامات المختلطة والعلامات والمصابيح الضوئية وحاملات اللافتات المتوفرة حاليًا في فئة اللافتات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006226, N'حاملي التوقيع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مصمم لعقد اللافتة. يحافظ الحامل على اللافتة محمية ، ولكنها مرئية للمستخدمين. يمكن تثبيته على حائط مثبت على سطح أو قائم بذاته.', N'باستثناء المنتجات مثل اللافتات المطبوعة مسبقًا واللافتات غير المطبوعة واللافتات المختلطة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006225, N'علامات ، تركيبة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كعلامة تحتوي على بعض العناصر المطبوعة مسبقًا ، ولكن بها أيضًا مساحة غير مطبوعة أو فارغة للمستخدم لتقديم معلومات أو تعليمات أو محتوى زخرفي. يمكن إضافة الصور أو الصياغة إلى اللافتة لنقل الرسالة. قد يتم تشغيلها أيضًا لتوفير الإضاءة ، مثل الإضاءة الخلفية أو النيون.', N'باستثناء المنتجات مثل اللافتات المطبوعة مسبقًا واللافتات غير المطبوعة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006223, N'لافتات مطبوعة مسبقًا', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كعلامة مطبوعة توفر معلومات أو إرشادات أو محتوى زخرفيًا. تحتوي هذه العلامات على صور أو صياغة مطبوعة مسبقًا لنقل رسالتها. يمكن أيضًا أن يتم تزويدهم بالطاقة لتوفير الإضاءة ، مثل النيون.', N'يستثني على وجه التحديد منتجات مثل اللافتات غير المطبوعة والإشارات المختلطة واللافتات الرقمية المشغلة حيث يُدخل المستخدمون الصياغة ليتم عرضها.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006224, N'لافتات غير مطبوعة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها علامة غير مطبوعة أو فارغة توفر معلومات أو إرشادات أو محتوى زخرفيًا. يمكن وضع الصور أو الصياغة على اللافتة لإيصال رسالة. قد تحتوي العلامة على أحرف مطبوعة مسبقًا يمكن ترتيبها لنقل الرسالة. قد يتم تشغيلها أيضًا لتوفير الإضاءة ، مثل الإضاءة الخلفية. يتضمن إشارات رقمية تعمل بالطاقة حيث يقوم المستخدمون بإدخال الكلمات ليتم عرضها على وجه التحديد', N'باستثناء منتجات مثل اللافتات المطبوعة مسبقًا ، والإشارات المختلطة ، وإشارات النيون.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001117, N'غلاف / غلاف الكمبيوتر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها صندوق بلاستيكي مصمم خصيصًا لاحتواء وحدة المعالجة المركزية (CPU) أو مزود الطاقة غير المنقطع (UPS). يشمل منتجات مثل الغلاف / الغلاف المستخدم لتحسين مظهر الكمبيوتر أو تغييره.', N'باستثناء منتجات مثل حقائب الكمبيوتر والأغطية والحوامل المضادة للكهرباء الساكنة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001118, N'مكونات الكمبيوتر - قطع غيار / ملحقات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قطع غيار وملحقات يمكن تطبيقها على مختلف مكونات الكمبيوتر.', N'يستثنى من ذلك جميع المنتجات الأخرى التي يتم تقديمها حاليًا ضمن قطاع أجهزة الكمبيوتر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001119, N'مكونات الكمبيوتر الأخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مكون كمبيوتر ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات الموجودة في قوالب موجودة داخل المخطط.', N'يستثني كافة مكونات الكمبيوتر المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001120, N'حزم متنوعة لمكونات الكمبيوتر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتجان مميزان أو أكثر من منتجات مكونات الكمبيوتر التي يتم بيعها معًا والموجودة في المخطط ولكنها تنتمي إلى قوالب مختلفة ، أي منتجين أو أكثر متضمنين في نفس الحزمة التي تتقاطع مع فئة مكونات الكمبيوتر. تشمل منتجات مثل ذاكرة الكمبيوتر واللوحات الأم للكمبيوتر التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل معالجات الكمبيوتر التي تُباع بشكل فردي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001121, N'تبريد الكمبيوتر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز لتبريد أجهزة الكمبيوتر ، بما في ذلك المنتجات التي تخلق تيارًا من الهواء أو نسيمًا للتبريد.', N'باستثناء أي منتجات غير مصممة خصيصًا لأجهزة الكمبيوتر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001122, N'ذاكرة الكمبيوتر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كشريحة ذاكرة دائرة متكاملة ، والتي يمكن أن تستخدمها البرامج لأداء المهام الضرورية أثناء تشغيل الكمبيوتر أو التي تسمح بتخزين المعلومات أو الوصول إليها. يمكن تخزين هذه المعلومات أو الوصول إليها بأي ترتيب ويمكن الوصول إلى جميع مواقع التخزين بشكل متساوٍ.', N'يستثني منتجات مثل أجهزة تخزين البيانات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001123, N'اللوحات الأم للكمبيوتر', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها لوحة الدوائر المطبوعة الأساسية في جهاز الكمبيوتر ، والتي تحتوي على جميع الدوائر والمكونات الأساسية اللازمة لتشغيل الكمبيوتر. تحتوي اللوحة الأم عادةً على ناقل النظام ، ومآخذ المعالج والمعالج المساعد ، ومآخذ الذاكرة ، والمنافذ التسلسلية والمتوازية ، وفتحات التوسعة ووحدات التحكم الطرفية ، ولكن يمكن بيعها مع أو بدون هذه المكونات المرفقة.', N'باستثناء منتجات مثل الأجهزة الطرفية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005683, N'تخزين ألعاب الكمبيوتر / الفيديو', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز تخزين كبير السعة يتم الاحتفاظ بالبيانات عليه ويمكن استرداد البيانات منه. تتضمن أجهزة مثل الأشرطة والخراطيش والأقراص المخصصة للاستخدام مع أجهزة الكمبيوتر ووحدات التحكم في الفيديو. تشمل منتجات مثل أقراص ZIP و JAZZ.', N'يستثني منتجات مثل ذاكرة الوصول العشوائي (RAM).')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001126, N'لوحات / بطاقات التوسع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها لوحة دوائر مطبوعة يمكن إدخالها في فتحة التوسعة للكمبيوتر من أجل إضافة وظائف رسومية و / أو صوتية إضافية بحيث يمكن للمرء مشاهدة التلفزيون أو تشغيل الموسيقى على الكمبيوتر. يتضمن منتجات مثل بطاقة توسيع الرسومات وبطاقة الصوت وبطاقة التلفزيون.', N'باستثناء منتجات مثل بطاقات الذاكرة ووسائط التخزين مثل خراطيش البيانات.')
GO
