INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006236, N'برامج كمبيوتر (غير ألعاب) - رقمية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كبرنامج يتم تنزيله أو دفقه لإرشاد الكمبيوتر إلى كيفية معالجة البيانات جنبًا إلى جنب مع الوثائق التي تشرح كيفية استخدام هذه البرامج. تتضمن منتجات مثل برامج معالجة النصوص وبرامج جداول البيانات وبرامج العروض التقديمية والخطوط وبرامج النشر المكتبي.', N'باستثناء منتجات مثل ألعاب الكمبيوتر وتطبيقات الهاتف المحمول الرقمية.')
