INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001137, N'برامج ألعاب الكمبيوتر / ألعاب الفيديو', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها برامج كمبيوتر مصممة لتوفير نشاط للترفيه أو التسلية. يشمل جميع برامج الألعاب على وسائط مختلفة مثل CD-ROM و Floppy Disc.', N'يستثني المنتجات الرقمية التي تم تنزيلها أو دفقها إلى العملاء. يستثني أيضًا منتجات مثل التطبيقات غير المتعلقة بالألعاب وبطاقات الذاكرة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006235, N'برامج ألعاب الكمبيوتر / ألعاب الفيديو - رقمي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها محتوى لعبة رقمية مصممة لتوفير نشاط للترفيه أو التسلية يتم تنزيله أو بثه. عادةً ما تكون مخصصة للاستخدام مع وحدة تحكم ألعاب و / أو تلفزيون أو كمبيوتر وقد تكون لعبة جديدة أو تحديثًا للعبة موجودة أو جزء صغير من لعبة مثل شخصية أو خريطة أو مستوى جديد. تتضمن ألعاب الفيديو التقليدية والألعاب الرياضية مثل الجولف وتعليمات DIY والتمارين الرياضية.', N'باستثناء البرامج غير المتعلقة بالألعاب ، أو تطبيقات الهاتف المحمول الرقمية ، أو برامج الألعاب الموجودة على الأقراص المضغوطة أو أقراص DVD المسجلة مسبقًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001139, N'كمبيوتر / برامج ألعاب فيديو أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها برامج ألعاب كمبيوتر / فيديو ، حيث لا يتمكن مستخدم المخطط من تصنيف المنتجات الموجودة في قوالب موجودة داخل المخطط.', N'يستثني جميع برامج ألعاب الكمبيوتر / الفيديو المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001140, N'حزم متنوعة لبرامج ألعاب الكمبيوتر / الفيديو', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما اثنان أو أكثر من منتجات برامج ألعاب الكمبيوتر / ألعاب الفيديو المميزة التي يتم بيعها معًا والموجودة في المخطط ولكنها تنتمي إلى قوالب مختلفة ، أي منتجين أو أكثر متضمنين في نفس الحزمة التي تتقاطع داخل الكمبيوتر / فئة برامج ألعاب الفيديو. تشمل منتجات مثل برامج الكمبيوتر التعليمية والألعاب التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل أجهزة الكمبيوتر / ألعاب الفيديو التي تُباع بشكل فردي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001141, N'أجهزة الكمبيوتر - قطع غيار / ملحقات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجزاء بديلة وملحقات يمكن تطبيقها على منتجات الكمبيوتر المختلفة.', N'يستثني جميع المنتجات الأخرى التي يتم تقديمها حاليًا ضمن قطاع أجهزة الكمبيوتر / ألعاب الفيديو.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001142, N'أجهزة كمبيوتر أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كمقالة كمبيوتر ، حيث لا يستطيع مستخدم المخطط تصنيف المنتجات الموجودة في قوالب موجودة داخل المخطط.', N'يستثني جميع أجهزة الكمبيوتر المصنفة حاليًا بالإضافة إلى المقالات التي يمكن العثور عليها ضمن قطاع الاتصالات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001143, N'المنظمون الإلكترونيون', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز كمبيوتر محمول مثل قواعد البيانات أو المنظمين أو القواميس أو المترجمين الذين ليس لديهم نظام تشغيل مفتوح (قابل للبرمجة).', N'باستثناء منتجات مثل أجهزة الكمبيوتر الشخصية المحمولة والأجهزة المحمولة والمساعدات الرقمية الشخصية.')
