INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006405, N'أجهزة الكمبيوتر الشخصية - الكل في واحد', N'الكمبيوتر الشخصي / الكل في واحد - يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز كمبيوتر شخصي مكتبي مع طاقة معالجة كافية وسعة تخزين للاستخدام المنزلي والمكتبي.', N'باستثناء منتجات مثل أجهزة الكمبيوتر الشخصية - سطح المكتب / طرفي الإنترنت ، وأجهزة الكمبيوتر الشخصية المحمولة ، والخوادم ، والأجهزة اللوحية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001144, N'أجهزة الكمبيوتر الشخصية - سطح المكتب / محطة الإنترنت', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز كمبيوتر شخصي مكتبي يتمتع بقدرة معالجة وسعة تخزين كافية للاستخدام المنزلي والمكتبي. يتضمن على وجه التحديد منتجًا مثل أجهزة سطح المكتب للإنترنت الطرفية.', N'باستثناء منتجات مثل أجهزة الكمبيوتر متعددة الإمكانات وأجهزة الكمبيوتر الشخصية المحمولة والخوادم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001145, N'أجهزة الكمبيوتر الشخصية - المحمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز كمبيوتر شخصي صغير يمكن استخدامه بسهولة في مواقع مختلفة ويمكن حمله بسهولة.', N'باستثناء منتجات مثل أجهزة الكمبيوتر المكتبية الشخصية غير المحمولة والأجهزة اللوحية / قارئات الكتب الإلكترونية والمساعدات الإلكترونية والمنظمين.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006276, N'أجهزة الكمبيوتر الشخصية - الأجهزة اللوحية / قارئات الكتب الإلكترونية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز لوحي يمكن استخدامه أثناء النقل (المشي ، وما إلى ذلك) ؛ يستخدم لوحة مفاتيح افتراضية في المقام الأول ؛ قد تستفيد من التخزين السحابي. تعمل بشكل عام على نظام تشغيل مختلف عن أجهزة الكمبيوتر المحمولة أو أجهزة الكمبيوتر المكتبية أو الهواتف الذكية. يعمل الجهاز اللوحي بشكل عام على تشغيل "تطبيقات" وليس "برامج". عادة ما تكون الشاشة من 6 إلى 13 بوصة. تتضمن منتجات مثل الأجهزة اللوحية وقارئات الكتب الإلكترونية والأردواز.', N'باستثناء المنظمين وأجهزة الكمبيوتر المحمولة / كمبيوتر الطاولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001146, N'المساعدات الرقمية الشخصية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز محمول باليد أو بحجم راحة اليد. المساعد الرقمي الشخصي عبارة عن أجهزة كمبيوتر صغيرة يمكن حملها واستخدامها لتخزين المعلومات. قد تجمع أيضًا بين ميزات الحوسبة والهاتف / الفاكس والإنترنت والشبكات.', N'باستثناء منتجات مثل المنظمين الإلكترونيين وأجهزة الكمبيوتر الشخصية المحمولة والهواتف الذكية.')
