INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001147, N'الخوادم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز كمبيوتر في شبكة توفر الوصول إلى أجهزة الكمبيوتر الأخرى في الشبكة إلى البرامج أو صفحات الويب أو البيانات أو الملفات والخدمات الأخرى ، مثل الوصول إلى الطابعة أو الوصول إلى الاتصالات.', N'باستثناء منتجات مثل أجهزة الكمبيوتر الشخصية غير المصممة خصيصًا للاستخدام كخوادم وكذلك خوادم لأتمتة المنزل أو الأجهزة الذكية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006743, N'الساعات الذكية', N'تشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ساعة ذكية. هذا النوع من الأجهزة عبارة عن ساعة معصم أو ساعة جيب محوسبة بوظائف تم تحسينها بما يتجاوز ضبط الوقت. قد تتضمن بعض الوظائف النموذجية ، على سبيل المثال لا الحصر ، القدرة على تشغيل تطبيقات الهاتف المحمول أو نظام تشغيل الهاتف المحمول ومشغل الوسائط المحمول (ملفات الراديو والصوت والفيديو) والقدرة الهاتفية المحمولة.', N'')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001370, N'حزم متنوعة من أجهزة الكمبيوتر / ألعاب الفيديو', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما اثنان أو أكثر من منتجات ألعاب الكمبيوتر / ألعاب الفيديو المميزة التي يتم بيعها معًا ، والتي توجد ضمن المخطط الذي ينتمي إلى فئات مختلفة ولكن لنفس العائلة ، أي منتجين أو أكثر متضمنين في نفس الحزمة التي تتقاطع فصول دراسية داخل عائلة أجهزة الكمبيوتر / ألعاب الفيديو. تشمل منتجات مثل أجهزة الكمبيوتر الشخصية وبرامج الكمبيوتر التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل حزم طابعة الكمبيوتر والماسح الضوئي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005763, N'ملحقات وحدة التحكم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أحد الملحقات التي يمكن تطبيقها على أجهزة ألعاب الفيديو على الكمبيوتر. يتضمن منتجات مثل ملصق وحدة تحكم ألعاب فيديو الكمبيوتر أو شاحن وحدة التحكم.', N'باستثناء المنتجات مثل الملحقات المصممة خصيصًا لجهاز الكمبيوتر.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10003817, N'أجهزة ألعاب فيديو - غير محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كوحدة تحكم ألعاب فيديو إلكترونية غير محمولة مصممة خصيصًا لعرض / تشغيل المحتويات التفاعلية لبرامج ألعاب ألعاب الفيديو من خلال شاشة تلفزيون أو شاشة.', N'يستثنى على وجه التحديد المنتجات مثل أجهزة ألعاب الفيديو المحمولة ذات الشاشات المدمجة. باستثناء منتجات مثل أجهزة الكمبيوتر الشخصية وشاشات الكمبيوتر.')
