INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007530, N'Broccoliplants (Brassica Oleracea var. Italica)', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنه الشكل المزروع غير المحصود من نباتات البروكلي المزروعة كخضروات براسيكا (نوع الملفوف) أو محاصيل البروكلي المزروعة من براسيكا أوليراسيا فار. إيطاليا.', N'يستثنى على وجه التحديد نباتات البروكلي المزروعة لأغراض أخرى غير الخضروات المصنوعة من الكرنب (نوع الملفوف) ، والأصناف الأخرى من هذا النوع والمنتجات المحصودة من نباتات البروكلي والمنتجات الثانوية مثل البروكلي الطازج أو الجاهز / المُصنّع أو غير المُجهز / غير المُعالج والمستقر أو المُجمد. كود EPPO: BRSOK')
