INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007529, N'نبتات بروكسل (Brassica Oleracea var. Gemmifera)', N' يشمل أي منتج يمكن وصفه / ملاحظته على أنه الشكل المزروع غير المحصود من براعم بروكسل المزروعة كخضروات براسيكا (نوع الملفوف) أو محاصيل براعم بروكسل المزروعة من براسيكا أوليراسيا فار. gemmifera.', N'يستثنى على وجه التحديد نباتات براعم بروكسل المزروعة لأغراض أخرى غير الخضروات البراسيكا (نوع الملفوف) ، والأصناف الأخرى من هذا النوع والمنتجات المحصودة من نبتات بروكسل والمنتجات الثانوية مثل النباتات الطازجة أو المحضرة / المُصنعة أو غير المُجهزة / غير المُجهزة والمستقرة أو المُجمدة من براعم بروكسل . كود EPPO: BRSOF')
