INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10007525, N'نباتات القرنبيط (Brassica Oleracea var. Botrytis)', N' يشمل أي منتج يمكن وصفه / ملاحظته على أنه الشكل المزروع غير المحصود من نباتات القرنبيط المزروعة كخضروات براسيكا (نوع الملفوف) أو محاصيل القرنبيط المزروعة من Brassica oleracea var. بوتريتيس.', N'يستثنى على وجه التحديد نباتات القرنبيط المزروعة لأغراض أخرى غير الخضروات المصنوعة من الكرنب (نوع الملفوف) ، والأصناف الأخرى من هذا النوع والمنتجات المحصودة من نباتات القرنبيط والمنتجات الثانوية مثل القرنبيط الطازج أو المُجهز / المُصنَّع أو غير المُجهز / غير المُجهز / غير المُجهز والمستقر أو المُجمد. كود EPPO: BRSOB')
