INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005757, N'الكابلات السمعية والبصرية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أسلاك أو ألياف بصرية مرتبطة أو ملتوية معًا في قطعة واحدة من الكبل تُستخدم لنقل التيار الكهربائي والبيانات الصوتية والمرئية من وإلى المعدات السمعية والبصرية. قد تحتوي هذه المنتجات أو لا تحتوي على سترة واقية أو غمد للعزل. تتضمن منتجات مثل كبلات scart وكابلات s-video وكابلات مكبرات الصوت وكابلات جاك.', N'يستثني على وجه التحديد جميع الأسلاك المجدولة وجميع كابلات البناء / البناء ، باستثناء المنتجات مثل كبلات الكمبيوتر وكابلات الاتصالات السلكية واللاسلكية وكابلات تركيب الأقمار الصناعية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005754, N'كابلات الكمبيوتر', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أسلاك أو ألياف بصرية مرتبطة أو ملتوية معًا في قطعة واحدة من الكبل تُستخدم لنقل التيار الكهربائي أو البيانات أو المعلومات من وإلى الكمبيوتر. قد تحتوي هذه المنتجات أو لا تحتوي على سترة واقية أو غلاف للعزل. يشمل منتجات مثل كبلات توصيل الكمبيوتر وكابلات الشبكة وكابلات USB.', N'يستثنى على وجه التحديد جميع الأسلاك المجدولة المفردة وجميع كابلات البناء / البناء. باستثناء منتجات مثل الكابلات السمعية والبصرية وكابلات الاتصالات السلكية واللاسلكية وكابلات تركيب الأقمار الصناعية وكابلات الطاقة وكابلات الاتصالات.')
