INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005759, N'كابلات تركيب الأقمار الصناعية', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أسلاك أو ألياف بصرية مرتبطة أو ملتوية معًا في قطعة واحدة من الكبل تُستخدم لنقل التيار الكهربائي وإشارات الأقمار الصناعية من معدات الأقمار الصناعية. قد تحتوي هذه المنتجات أو لا تحتوي على سترة واقية أو غلاف للعزل. يشمل منتجات مثل الكابلات الهوائية للأقمار الصناعية.', N'يستثنى على وجه التحديد جميع الأسلاك المجدولة المفردة وجميع كابلات البناء / البناء. باستثناء منتجات مثل كبلات الكمبيوتر والكابلات السمعية والبصرية وكابلات الاتصالات السلكية واللاسلكية.')
