INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005583, N'لوحات التوزيع', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية تستخدم لتأمين المعدات الكهربائية و / أو معدات الشبكة. تشمل المنتجات التي تتطلب التجميع أو تأتي مُجمَّعة مسبقًا مع قواطع وعوازل.', N'باستثناء المنتجات مثل الكتل الطرفية والشرائط وقواطع الدائرة التي تُباع بشكل منفصل.')
