INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005577, N'ملحقات / تركيبات التوزيع الكهربائي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كملحق أو تركيب يستخدم للمساعدة أو تحسين تركيب منتجات التوزيع الكهربائي. تشمل منتجات مثل مجموعات الأسلاك الطرفية وأغطية الكتل الطرفية وفواصل الكتل الطرفية.', N'باستثناء المنتجات مثل الزردية المصنفة بالأدوات اليدوية وألواح الحائط.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000549, N'الصمامات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها سلك أو قضيب أو شريط من معدن قابل للانصهار يتم إدخاله للسلامة في دائرة كهربائية. عندما يزيد التيار عن قوة آمنة معينة ، يذوب المعدن ، مما يقطع الدائرة وبالتالي يمنع احتمال حدوث تلف. يشمل فيوزات التوصيل وصمامات دائرة الطاقة ؛ صمامات تأخير الوقت والصمامات سريعة المفعول ؛ الصمامات ذات الجهد المنخفض والصمامات ذات الجهد العالي.', N'يُستثنى من ذلك المنتجات مثل قواطع الدائرة والمقابس التي تُباع كاملة مع الصمامات وصمامات السيارات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005682, N'موقتات / وحدات تحكم كهربائية متعددة الاستخدامات / عالمية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز كهربائي يعمل آليًا / يتحكم في توقيت التشغيل لعدد من الأنظمة المنزلية المختلفة مثل الإضاءة الكهربائية والتدفئة المركزية وتسخين المياه وأنظمة الأمن والمعدات السمعية والبصرية. يشمل المنتجات ذات التوصيل الإضافي بالإضافة إلى المفاتيح الرقمية اللاسلكية القابلة للبرمجة ولوحات اللمس.', N'باستثناء منتجات مثل مؤقتات الطهي والساعات وأنظمة التحكم المخصصة لتطبيق واحد مثل منظمات الحرارة وأجهزة التحكم عن بُعد الصوتية والمرئية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005570, N'المرحلات / الملامسات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز تبديل كهربائي يفتح ويغلق تحت سيطرة دائرة كهربائية أخرى. تم تصميم المرحلات لتبديل الأحمال أو إيقاف تشغيلها ، في حين أن الموصلات مصممة "لكسر" حمل تيار عالٍ وعادة ما تكون مزودة بحماية من الحمل الزائد. يتضمن منتجات مثل مرحلات الطاقة ومرحلات المقبس ومرحلات الجهد المتناوب ، بالإضافة إلى موصلات الفراغ.', N'يستثنى من ذلك المنتجات مثل المفاتيح والمحولات وقواطع الدائرة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005568, N'المقسمات', N'يشمل أي منتج يمكن وصفه / ملاحظته كجهاز يفصل الإشارة الإلكترونية إلى إشارتين أصغر أو أكثر متساوية تقريبًا ويوزعها على عدد من أجهزة الاستقبال في وقت واحد. يشمل منتجات مثل مقسمات الطاقة ، ومقسمات إشارة الترددات اللاسلكية (ترددات الراديو) ومقسمات الميكروفون.', N'باستثناء منتجات مثل المحولات الكهربائية وخطوط النقل وأدوات ربط الكابلات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005585, N'كوابح / حماة تصاعد', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يستخدم لحماية الأجهزة الكهربائية والمعدات الإلكترونية. تعمل واقيات زيادة التيار على تنظيم الجهد الموفر للجهاز الكهربائي إما عن طريق منع أو تقصير الجهد الأرضي فوق عتبة آمنة. يشمل منتجات مثل مثبطات زيادة التيار الخارجية ، والتي يتم تثبيتها على اللوحة الكهربائية لحماية الأجهزة المنزلية مثل الثلاجات والغسالات ، أو مثبطات زيادة التيار في نقطة الاستخدام ، والتي يتم توصيلها بقطع فردية من المعدات الإلكترونية المنزلية مثل أجهزة الكمبيوتر وأجهزة التلفزيون .', N'يستثنى من ذلك المنتجات مثل قواطع التيار الكهربائي والصمامات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006896, N'الإنارة الكهربائية - أخرى', N'يشمل أي منتجات قد يتم وصفها / ملاحظتها على أنها منتجات إضاءة كهربائية ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'')
GO
