INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000593, N'عبوات متنوعة من المشروبات غير الكحولية - غير جاهزة للشرب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها نوعان أو أكثر من المشروبات غير الكحولية المميزة غير الجاهزة للشرب التي تباع معًا ، والتي توجد داخل المخطط والتي تنتمي إلى قوالب مختلفة ولكن تنتمي إلى نفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تعبر الطوب داخل فئة المشروبات غير الكحولية - ليست جاهزة للشرب. تشمل منتجات مثل مسحوق الشعير والميلك شيك التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك منتجات مثل ميلك شيك البودرة وعلب شوجر كاندي المتنوعة.')
