INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000220, N'عصير فواكه - جاهز للشرب (رف ثابت)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مشروبات جاهزة للشرب ، أو مشروبات غازية أو غير غازية مصنوعة حصريًا من محتوى السوائل المستخرج من الفاكهة أو مزيج من عصير الفاكهة والخضروات ، والتي تحتوي على الفاكهة باعتبارها الخاصية الرئيسية. تمت معالجة هذه المنتجات أو تعبئتها بطريقة تطيل عمرها الاستهلاكي. تشمل المنتجات أنواع العصائر النقية الفردية والمخلوطة.', N'يستثنى من ذلك المنتجات مثل العصائر الجاهزة للشرب والقابلة للتلف والمجمدة والعصائر غير الجاهزة للشرب ، ومشروبات الأطفال المتخصصة بالإضافة إلى عصير الخضار والخلطة / عصير الفاكهة التي تحتوي على الخضار باعتبارها السمة الرئيسية.')
