INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000594, N'عبوات متنوعة للمشروبات غير الكحولية - جاهزة للشرب', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما اثنان أو أكثر من المشروبات غير الكحولية الجاهزة للشرب التي تباع معًا ، والتي توجد داخل المخطط والتي تنتمي إلى قوالب مختلفة ولكن تنتمي إلى نفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة والتي عبور الطوب داخل فئة المشروبات غير الكحولية - جاهزة للشرب. تشمل منتجات مثل الكولا الجاهزة للشرب والثلج المباعة معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'لا يشمل المنتجات مثل علب الكولا ورقائق البطاطس الجاهزة للشرب.')
