INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000266, N'المنشطات / مشروبات الطاقة - جاهزة للشرب', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مشروبات جاهزة للشرب تحتوي على جلوكوز أو كافيين أو منبهات أخرى مصممة للاستهلاك مثل المشروبات الغازية. المنتجات متوفرة في مجموعة متنوعة من النكهات.', N'يستثني على وجه التحديد المشروبات المنكهة التي تحتوي على مادة الكافيين (مثل الكولا) ولكن لا يتم تصنيفها وتسويقها بخصائص تعزيز الطاقة. باستثناء منتجات مثل منتجات الطاقة / المنبهات والمنشطات / مشروبات الطاقة - غير الجاهزة للشرب وجميع مشروبات معالجة الجفاف.')
