INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000287, N'ألواح حبوب / موسلي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كتلة أو شريط يجمع بين كل أو بعض المكونات التالية ؛ شوفان ، موسلي ، قمح ، أرز ، نخالة ، بذور ، فواكه ، مكسرات ، حبوب وعسل. قد تحتوي المنتجات على شوكولاتة أو مغطاة بالكامل أو جزئيًا بالشوكولاتة. ربما تمت معالجة هذه المنتجات أو تعبئتها بطريقة تطيل عمرها الاستهلاكي. تشير هذه المنتجات في بعض الأحيان إلى فوائد مثل المكونات الطبيعية أو نسبة عالية من الألياف أو الفيتامينات أو البروتين أو الطاقة أو منخفضة الدهون.', N'يستثنى من ذلك منتجات مثل الحبوب وألواح الشوكولاتة والكعك والبسكويت والبسكويت.')
