INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008059, N'قضبان البروتين', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أشرطة غذائية تحتوي على نسبة عالية من البروتين إلى الكربوهيدرات / الدهون. وتتمثل مهمتها في المساعدة على التعافي بشكل أسرع بعد ممارسة رياضات التحمل ، بالإضافة إلى ذلك ، لأن هذه القضبان غالبًا ما تكون مليئة أيضًا بالفيتامينات والمعادن ، فهي بديل جيد للوجبات للأشخاص الذين يرغبون في إنقاص الوزن.', N'')
