INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006390, N'علكة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كنوع من الصمغ المصنوع من شيكل ، أو منتج لاتكس طبيعي ، أو مكافئ اصطناعي مثل بولي إيزوبوتيلين. تشمل علكة الفقاعات ، وهي نوع من العلكة ، مصممة بحيث يتم نفخها من الفم على شكل فقاعة.', N'يستثني العلكة غير المضغ والعلاجات المضادة للتدخين ، والمنتجات التي تدعي أنها في الأساس رعاية صحية أو لثة ذات أغراض محددة مثل العناية بالفم مثل تبييض الأسنان وتنظيفها.')
