INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000045, N'الشوكولاته والشوكولاته / مجموعات حلوى السكر - الحلويات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء من شوكولاتة أو حلويات بديلة للشوكولاتة أو عنصر حلويات عبارة عن مزيج من الشوكولاتة وحلوى السكر التي قد تحتوي على مكونات إضافية مثل الفوندان ، النوجا ، المرزبانية ، الكراميل ، كريم الحلوى ، المكسرات وجوز الهند والفواكه المجففة والحشوات اللينة الأخرى. يمكن بيع هذه المنتجات بشكل فردي أو تعبئتها لتوفير كمية من العناصر المتشابهة.', N'باستثناء منتجات مثل منتجات حلوى السكر النقية التي لا تحتوي على طبقة من الشوكولاتة. يستثنى من ذلك منتجات مثل طبخ الشوكولاتة.')
