INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000188, N'قشطة مجمدة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كريمة ألبان مجمدة (الجزء الدهني من الحليب الطازج الذي يرتفع إلى الأعلى إذا سمح له بالوقوف). يجب تجميد هذه المنتجات لإطالة عمرها الافتراضي. تشمل المنتجات الكريمة مع نكهات أو مكونات مضافة ، مثل الفاكهة والشوكولاتة.', N'يستثنى من ذلك منتجات مثل كريم الرف المستقر والقابل للتلف وجميع بدائل الكريم.')
