INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006975, N'بديل الكريم (مجمد)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كبديل للقشدة المجمدة ، وعادة ما تكون مصنوعة من زيوت نباتية ، مع مثبتات ومستحلبات ، والتي تم تصميمها وتسويقها خصيصًا لتكرار الكريمة الطبيعية. يجب تجميد هذه المنتجات لإطالة عمرها الافتراضي. تشمل المنتجات بدائل الكريمة مع نكهات أو مكونات مضافة ، مثل الفاكهة والشوكولاتة.', N'يستثنى من ذلك المنتجات مثل جميع بدائل الكريمات الثابتة والقابلة للتلف الكريمي والأرفف.')
