INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000277, N'زبادي (مثلج)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أغذية مجمدة مصنوعة من حليب حيواني كثيف بالتخمير ببكتيريا اللاكتيك. تشمل المنتجات اللبن الزبادي. يجب تجميد هذه المنتجات لإطالة عمرها الافتراضي. يشمل ذلك الزبادي المنكه (مثل المنكه بالفواكه و / أو الفانيليا و / أو المكسرات و / أو العسل) ، أو المعبأ بكمية صغيرة من الفاكهة المحفوظة أو المكسرات أو أي مرافقة أخرى.', N'باستثناء منتجات مثل شرب الزبادي والزبادي المستقر والقابل للتلف وجميع بدائل الزبادي ومنتجات الألبان والمشروبات البديلة والحلويات.')
