INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000041, N'دهون صالحة للأكل - حيوانية (قابلة للتلف)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة صلبة مصنوعة من ذوبان الدهون الحيوانية وتنقيتها ثم السماح لها بالتصلب عند التبريد. هذه المنتجات مصممة للطبخ والخبز. يمكن / يجب تبريد هذه المنتجات لإطالة عمرها الاستهلاكي. وتشمل المنتجات شحم الخنزير ، والتقطير ، والسمن ، والشحم.', N'يستثنى من ذلك منتجات مثل دهون الطهي النباتية والسمن النباتي والزبدة أو الزبدة القابلة للدهن.')
