INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000608, N'عبوات متنوعة من الدهون الصالحة للأكل', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما اثنان أو أكثر من الدهون الصالحة للأكل التي تباع معًا ، والتي توجد داخل المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمنين في نفس العبوة والتي تتقاطع مع الطوب داخل فئة الدهون الصالحة للأكل. يشمل منتجات مثل Lard و Dripping التي تباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك منتجات مثل المارجرين والزيوت النباتية.')
