INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000609, N'عبوات متنوعة من الزيوت / الدهون الصالحة للأكل', N'يشمل أي منتج يمكن وصفه / ملاحظته على أنهما اثنان أو أكثر من زيوت الطعام أو الدهون المميزة التي تباع معًا والموجودة في المخطط ولكنها تنتمي إلى فئات مختلفة ، أي منتجين أو أكثر متضمنين في نفس العبوة والتي تتقاطع مع فئات الزيوت والدهون عائلة صالحة للأكل. يشمل منتجات مثل عبوات الشحم بالزيت النباتي المتنوعة. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك المنتجات مثل عبوات شحم الخنزير والسمن والزيوت النباتية المتنوعة.')
