INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000197, N'حلويات (قابلة للتلف)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كطعام جاهز يتم استهلاكه عادة كطعام أخير من الوجبة. يجب تبريد هذه المنتجات لإطالة عمرها الافتراضي. تشمل المنتجات الحلويات التي تعتمد على البسكويت ، والخبز ، والكيك / الإسفنج ، ومنتجات الألبان ، والمعجنات ، والأرز.', N'يستثنى من ذلك منتجات مثل الحلويات المجمدة والحلويات المستقرة والمعجنات الآيس كريم والكعك والزبادي.')
