INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000611, N'عبوات متنوعة للحلويات / الحلويات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها نوعان أو أكثر من الحلويات المميزة أو الصلصات / الإضافات المباعة معًا ، والتي توجد داخل المخطط والتي تنتمي إلى طوب مختلف ولكن إلى نفس الفئة ، أي منتجان أو أكثر موجودان في نفس العبوة والتي عبر الطوب داخل فئة الحلويات والحلويات. تشمل منتجات مثل الآيس كريم وصلصة الحلوى التي تُباع معًا. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثنى من ذلك المنتجات مثل عبوات الآيس كريم والويفر المتنوعة وحزم متنوعة من الآيس كريم والدجاج كورما.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000215, N'أيس كريم / مستجدات مثلجات (مجمدة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أغذية محضرة حلوة مصنوعة من حليب الحيوانات أو بدائل الحليب أو الماء ، والتي يتم تجميدها وتحليتها وغالبًا ما يتم إضافة النكهة إليها بإضافة مكونات أخرى ، مثل الشوكولاتة والفانيليا والفاكهة. يجب تجميد هذه المنتجات لإطالة عمرها الافتراضي. تشمل مستجدات الآيس كريم وكيكات الآيس كريم والآيس كريم بأشكال مختلفة مثل الحاويات ومخاريط الآيس كريم المحشو والمصاصات المثلجة والمصاصات.', N'يستثنى من ذلك منتجات مثل Shelf Stable Ice Cream و Ice Cream Novelties و Ice Cream Based Desserts.')
