INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000134, N'تبغ - مضغ / شم', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جزء من تبغ كثيف ، وأحيانًا رطب ، وغير مدخن مصمم خصيصًا للاستنشاق أو وضعه مباشرة في الفم لامتصاصه أو مضغه. يشمل على وجه التحديد التبغ الاسكندنافي الناعم والتبغ الذي يحتوي على نكهات إضافية (مثل اليانسون والقرفة والنعناع والأعشاب.).', N'يستثني منتجات مثل جميع أنواع تدخين التبغ.')
