INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000267, N'التبغ - سائب', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها تبغ سائب أو مبشور مسبقًا ، يتم تقطيعه ومعالجته مسبقًا. يشمل التبغ المعبأ سائبًا والمصمم ليتم لفه في سيجارة يدويًا جنبًا إلى جنب مع أوراق السجائر ويمكن أيضًا استخدامه مع ماكينات لف السجائر. يشمل التبغ السائب الذي يمكن استخدامه في أي نوع من الأنابيب.', N'يستثنى من ذلك منتجات مثل تبغ الأنابيب الصلبة وتبغ المضغ والسعوط والسجائر.')
