INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005258, N'زجاجات / علب وقود الغاز (فارغة)', N' يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية تخزين فارغة ، والتي تم تصميمها خصيصًا بحيث تكون قابلة لإعادة الملء وتحتفظ بوقود الغاز المضغوط وتوزعه. عادة ما تكون أسطوانية الشكل ومصنوعة من المعدن. تشمل منتجات مثل زجاجات / علب الغاز القابلة لإعادة التعبئة التي تُستخدم مع مواقد التخييم ومواقد اللحام.', N'يستثنى على وجه التحديد زجاجات الغاز / العلب / الخزانات المستخدمة لتخزين الغازات غير القابلة للاحتراق مثل خزانات الغوص التي تحتوي على الهواء المضغوط. باستثناء المنتجات مثل حاويات الغاز غير القابلة لإعادة التعبئة والحاويات المستخدمة لتخزين الوقود السائل ومواد التشحيم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005306, N'زجاجات / حاويات الوقود السائل (فارغة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها حاوية تخزين فارغة ، والتي تم تصميمها خصيصًا بحيث تكون قابلة لإعادة التعبئة وتحتفظ بكمية من الوقود السائل وتوزعها. عادة ما تكون أسطوانية الشكل ومصنوعة من المعدن أو البلاستيك. يشمل منتجات مثل زجاجات / علب الوقود السائل القابلة لإعادة التعبئة التي تُستخدم مع مواقد التخييم وعلب البنزين / البنزين.', N'يستثنى على وجه التحديد جميع زجاجات / أوعية تخزين السوائل المستخدمة لتخزين السوائل غير القابلة للاحتراق. باستثناء المنتجات مثل جميع الزجاجات / الحاويات السائلة المستخدمة لتخزين السوائل غير الوقودية القابلة للاحتراق وزجاجات / علب الغاز السائل.')
