INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005255, N'إضافات الوقود', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها مادة كيميائية تضاف ، عادة بكميات صغيرة ، مباشرة إلى وقود لتحسين أو تحسين أو تقوية خصائص الأداء ، مثل لزوجته. يمكنه أيضًا إضافة خصائص جديدة إلى الوقود ، مما يمنحه ميزات لا تتوفر في العادة. تشمل منتجات مثل مكثفات الوقود والمواد المضافة المضادة للتآكل.', N'يستثنى على وجه التحديد مزيلات شحوم المحرك. يستثنى من ذلك منتجات مثل الوقود ومواد التشحيم والمركبات الواقية غير المصممة خصيصًا لإضافتها إلى الوقود.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005262, N'عبوات متنوعة الوقود / إضافات الوقود', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها نوعان أو أكثر من منتجات الوقود / إضافات الوقود المتميزة المباعة معًا والتي توجد في المخطط ولكنها تنتمي إلى فئات مختلفة ، أي منتجين أو أكثر متضمنين في نفس العبوة التي تحتوي على فئات متقاطعة داخل الوقود / عائلة إضافات الوقود. يشمل منتجات مثل عبوات الوقود السائل والمواد المضافة للوقود. يجب إزالة العناصر التي يتم استلامها مجانًا مع عمليات الشراء من عملية اتخاذ قرار التصنيف.', N'يستثني منتجات مثل الوقود المباع مع الإضافات المضافة بالفعل كجزء من الصيغة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000543, N'أدوات / مباريات إطفاء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة / أداة لإشعال النار. تشمل منتجات مثل الولاعات وأعواد الثقاب ولاعات الشواء بالغاز.', N'يستثنى من ذلك منتجات مثل الجل والوقود السائل والصلب ، وكذلك الأخشاب التي لا يُقصد استخدامها كوقود.')
