INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006407, N'غاز ثاني أكسيد الكربون', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ثاني أكسيد الكربون (CO2). يستخدم ثاني أكسيد الكربون كعامل ضغط لعناصر مثل المشروبات أو طراز / مركبات التحكم عن بعد ، ومسدسات الحبيبات / كرات الطلاء.', N'')
