INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000449, N'الإسعافات الأولية - الإكسسوارات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها ملحقات للإسعافات الأولية ، مصممة خصيصًا للاستخدام مع منتجات الإسعافات الأولية لتعزيز أو استكمال سلامة وراحة وراحة المريض.', N'باستثناء منتجات مثل ملحقات الإسعافات الأولية التي يتم الحصول عليها بوصفة طبية فقط أو من أخصائي الرعاية الصحية والضمادات والضمادات واللاصقات.')
