INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000525, N'تجهيزات الأذن', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كعنصر رعاية صحية أو مستحضر أو دواء مصمم خصيصًا للوقاية من الأمراض أو الاضطرابات التي تصيب الأذن أو علاجها أو التخفيف من حدتها. يشمل المنتجات الاصطناعية.', N'يستثنى على وجه التحديد شموع إزالة الشمع ومستحضرات إزالة الشمع. باستثناء منتجات مثل مستحضرات الأذن التي يتم الحصول عليها فقط بوصفة طبية أو من أخصائي الرعاية الصحية ومنتجات الرعاية الصحية الأخرى التي تستهدف مناطق أخرى من الجسم.')
