INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000529, N'الأجهزة البصرية - العدسات اللاصقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كزوج من العدسات البصرية الدائرية الرفيعة جدًا ، والمصممة خصيصًا للمساعدة في تصحيح البصر المعيب ، وعادة ما يتم تشكيلها لتناسب مباشرة قرنية العين. تشمل المنتجات العدسات اللاصقة للاستخدام اليومي ، والعدسات اللاصقة التي تستخدم لمرة واحدة ، والعدسات اللاصقة ذات الاستخدام المطول ، والعدسات اللاصقة اللينة ، والعدسات الصلبة المنفذة للغازات.', N'باستثناء منتجات مثل العدسات اللاصقة التي تم الحصول عليها فقط بوصفة طبية أو من أخصائي رعاية صحية ونظارات النظارات والنظارات والعناية بالأجهزة البصرية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000639, N'الأجهزة البصرية - إطارات النظارات', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كإطار بصري مصمم خصيصًا لإيواء عدسات النظارات ، والتي تساعد في تصحيح البصر المعيب. يشمل فقط الإطارات بدون عدسات مرفقة.', N'يستثني على وجه التحديد النظارات الجاهزة. باستثناء منتجات مثل Spectacle Frames التي تم الحصول عليها بوصفة طبية أو من أخصائي رعاية صحية والعدسات اللاصقة وعدسات النظارات والنظارات الشمسية والنظارات المكبرة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000638, N'الأجهزة البصرية - عدسات النظارات', N' تشمل أي منتجات يمكن وصفها / ملاحظتها كعدسة بصرية مصممة خصيصًا لمساعدة وتصحيح عيوب البصر ، وتهدف إلى تركيبها في إطارات النظارات. يشمل فقط العدسات بدون إطار مرفق.', N'يستثنى على وجه التحديد العدسات اللاصقة. باستثناء منتجات مثل عدسات Spectacle التي يتم الحصول عليها بوصفة طبية أو من أخصائي رعاية صحية ، ونظارات Spectacles الجاهزة للارتداء ، وإطارات النظارات ، والنظارات الشمسية ، والنظارات المكبرة.')
