INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000637, N'أجهزة بصرية - نظارات شمسية - جاهزة للارتداء', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة بصرية مصممة خصيصًا لحماية العينين من أشعة الشمس ، وتتكون عادةً من زوج من العدسات الملونة المركبة داخل إطار يرتديه المستخدم وذلك لوضع العدسات في المقدمة مباشرةً من العيون. يشمل فقط النظارات الشمسية الكاملة الجاهزة للارتداء.', N'باستثناء منتجات مثل النظارات الشمسية الجاهزة للارتداء التي يتم الحصول عليها فقط بوصفة طبية أو من أخصائي الرعاية الصحية ، والنظارات الجاهزة للارتداء ، وعدسات النظارات ، وإطارات النظارات ، والعدسات اللاصقة ، والنظارات المكبرة.')
