INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000527, N'العناية بالأجهزة البصرية - العدسات اللاصقة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كريم أو جل أو سائل يتم استخدامه لتنظيف جميع أنواع العدسات اللاصقة والعناية بها والعناية بها مثل الحالات المحددة.', N'باستثناء منتجات مثل مستحضرات / علب العناية بالعدسات اللاصقة التي يتم الحصول عليها فقط بوصفة طبية أو من أخصائي رعاية صحية ، والعدسات اللاصقة ، وجميع مستحضرات العناية بالعيون ومنتجات العناية بالعدسات والملحقات.')
