INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000688, N'العناية بالأجهزة البصرية - نظارات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها كريم أو جل أو سائل لتنظيف جميع أنواع النظارات والعناية بها.', N'باستثناء منتجات مثل Spectacle Care Preparations التي يتم الحصول عليها فقط بوصفة طبية أو من أخصائي رعاية صحية والعدسات اللاصقة وسلاسل وحافظات النظارات وجميع مستحضرات العناية بالعين.')
