INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000689, N'إكسسوارات العناية بالنظارات', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها منتج يحمي النظارات ويعتني بها مثل العلب الخاصة وسلاسل النظارات.', N'باستثناء منتجات مثل Spectacle Cleaning and Care ، وجميع مستحضرات العناية بالعين.')
