INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10000820, N'مساعدات تغذية الاطفال (بالطاقة)', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز يعمل بالطاقة والذي يُقصد به أن يكون بمثابة مساعدة في عملية تغذية الأطفال. يشمل منتجات مثل أجهزة تسخين الرضّاعات ومعقمات الرضّاعات.', N'يستثنى على وجه التحديد جميع المنتجات التي لا تعمل بالطاقة والتي تساعد في تغذية الطفل. باستثناء المنتجات مثل الزجاجات نفسها وأي أدوات تغذية مثل الأطباق وأدوات المائدة وجميع المرايل والحصير ومنتجات الحماية الأخرى.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002000, N'فتاحات علب (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز كهربائي حيث تقطع الشفرات المزودة بالطاقة إلكترونيًا على طول حافة العلبة لإزالة الجزء العلوي منها. يشمل منتجات مثل فتاحات العلب الكهربائية بدون استخدام اليدين والمثبتة على السطح.', N'باستثناء منتجات مثل فتاحات العلب اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002019, N'ماكينات كاندي فلوس', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز إلكتروني مصمم لإنشاء حلوى حلوى من السكر المحبب وملونات الطعام عن طريق وضع المكونات في وعاء ساخن ودوران وتشغيل عصا حول الحافة التي يلتصق بها السكر.', N'باستثناء منتجات مثل صناع كاندي فلوس الصناعي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002016, N'صناع المشروبات الغازية', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز مصمم لتمرير ثاني أكسيد الكربون عبر سائل من أجل تكوين فقاعات بداخله وإحداث تأثير "فوار". يشمل منتجات مثل Soda-Streams.', N'يستثنى من ذلك منتجات مثل صانعات المشروبات غير الغازية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005690, N'نوافير الشوكولاتة (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز لتقديم الشوكولاتة. تتكون نوافير الشوكولاتة من طبقات متعددة فوق قاعدة حاويات. تذوب الشوكولاتة بالحرارة من الأسفل ، ثم تُنقل عموديًا إلى أعلى النافورة بواسطة المثقاب اللولبي. بمجرد وصول الشوكولاتة إلى القمة ، تتدفق الشوكولاتة إلى أسفل جوانب الهيكل مما يسمح للمستخدم بغمس المواد الغذائية فيه ويتكرر هذا النمط. تشمل منتجات مثل نوافير الشوكولاتة البلاستيكية وكذلك نوافير الشوكولاتة المعدنية.', N'باستثناء منتجات مثل الفوندو والطاجين وصواني التدفئة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006852, N'محامص حبوب البن', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مصمم لتحميص / طهي حبوب القهوة عن طريق وضعها في أسطوانة لتحميصها قبل استهلاكها (سواء كانت تختمر أو تأكل).', N'يستثنى من ذلك منتجات مثل المشواة الغازية والمشاوي الغذائية / المحمصات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002006, N'مطاحن القهوة (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز مصمم لتقليل حبوب البن إلى حبيبات من أجل صنع مشروب. يتم تحقيق ذلك إما عن طريق آلية مطحنة أو شفرات دوارة. يمكن أن تكون هذه المنتجات يدوية أو تلقائية.', N'يستثنى من ذلك منتجات مثل مطاحن اللحوم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005358, N'بنادق ملفات تعريف الارتباط (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز إلكتروني مصمم لضغط العجين في أشكال ملفات تعريف الارتباط التي يمكن طهيها في الفرن. تم تصميم هذه الأجهزة لتقليل جهد المستخدم للسماح بملفات تعريف الارتباط بأشكال وأحجام مختلفة. يشمل منتجات مثل مسدسات ملفات تعريف الارتباط التي تعمل بالبطارية.', N'باستثناء منتجات مثل مسدسات ملفات تعريف الارتباط اليدوية ومسدسات التسخين المستخدمة في الأعمال اليدوية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002018, N'المجففات (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز مصمم لتجفيف الطعام وحفظه عن طريق تجفيفه. عادة ، يتم وضع الطعام في غرفة مضغوطة ويتم دفع الهواء الساخن عبرها.', N'يستثنى من ذلك منتجات مثل السخانات والأفران.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002022, N'عبوات متنوعة للأغذية / المشروبات', N'يشمل أي من المنتجات التي يمكن وصفها / ملاحظتها على أنها منتجان مميزان أو أكثر من منتجات أجهزة تحضير الطعام / المشروبات التي تباع معًا ، والتي توجد ضمن المخطط الذي ينتمي إلى طوب مختلف ولكن لنفس الفئة ، أي منتجين أو أكثر متضمن في نفس العبوة والتي عبر الطوب داخل فئة أجهزة تحضير الطعام / المشروبات. تشمل منتجات مثل صانعات الآيس كريم التي تُباع مع أجهزة صنع الزبادي. يجب إزالة العناصر المستلمة مجانًا مع الشراء من عملية اتخاذ قرار التصنيف.', N'باستثناء منتجات مثل الغلايات التي تباع مع محمصات الخبز.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002020, N'أجهزة تحضير الطعام / المشروبات أخرى', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أحد منتجات أجهزة تحضير الطعام ، حيث لا يكون مستخدم المخطط قادرًا على تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'يستثنى من ذلك جميع منتجات أجهزة تحضير الطعام المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005689, N'ماكينات صنع المشروبات المجمدة / ماكينات حلاقة الثلج (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / مراقبتها كجهاز إلكتروني مصمم إما لتحضير شراب قائم على الجليد ، أو إنتاج رقائق الثلج أو توفير مزيج من هذه الوظائف. يتم تشغيل هذه المنتجات بالكهرباء وهي متوفرة في مجموعة متنوعة من التصاميم. تشمل منتجات مثل ماكينات صنع مخروط الثلج وآلات حلاقة الثلج.', N'باستثناء منتجات مثل كسارات الثلج وآلات صنع مكعبات الثلج وموزعات المياه وآلات إزالة الجليد غير العاملة بالطاقة وآلات صنع مخروط الثلج غير العاملة بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002005, N'بشر (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز إلكتروني ، وتتكون عادةً من سماعة تحتوي على تجويف للجبن أو غيرها من المواد الغذائية وأسطوانة دوارة من الفولاذ المقاوم للصدأ يمكن بشرها.', N'يستثنى من ذلك منتجات مثل أجهزة التبشير اليدوية ، والفرم الكهربائية والمعالجات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002011, N'صانعات المشروبات الساخنة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كآلة مصممة لإنتاج مشروب ساخن إما من خلال استخدام نظام ضغط مرتفع كما هو موضح في آلة إسبرسو أو نظام ضخ كما هو مستخدم في تصفية القهوة. يمكن تصنيع هذه الآلات من مواد مختلفة وعادة ما تقدم أكثر من مشروب ساخن. تشمل منتجات مثل آلات تحضير القهوة المفلترة وآلات صنع الإسبريسو وآلات صنع الشاي وآلات صنع القهوة الفورية بالإضافة إلى آلات صنع القهوة والموكا. على وجه التحديد', N'يستثنى على وجه التحديد الغلايات. يستثنى من ذلك منتجات مثل العصارات وماكينات صنع المشروبات الغازية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002013, N'صانعات الآيس كريم (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز مصمم لخلط المكونات في الآيس كريم ، عن طريق مزجها بمجداف في وعاء معزول ومجمد. تشمل منتجات مثل صانعات الآيس كريم اليدوية وآلات صنع الآيس كريم الأوتوماتيكية وآلات صنع الآيس كريم المزدوجة / الزبادي المجمد.', N'باستثناء منتجات مثل ماكينات صنع الزبادي المجمد.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005357, N'كسارات الثلج / صانعات مكعبات الثلج (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز إلكتروني مصمم لإنشاء مكعبات ثلج أو سحق ثلج استعدادًا لإضافته إلى مشروب أو وصفة. هذه المنتجات متوفرة بأحجام مختلفة حسب متطلبات المستهلك. يشمل منتجات مثل صانعات مكعبات الثلج المصنوعة من الفولاذ المقاوم للصدأ.', N'باستثناء منتجات مثل ماكينات صنع مكعبات الثلج اليدوية وآلات طحن الثلج.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002007, N'عصارات (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة كهربائية مصممة لاستخراج العصير من الحمضيات أو ما شابه ذلك عن طريق سحقها. يتم جمع العصير في جسم الجهاز وصرفه من صنبور أو صنبور. تشمل منتجات مثل العصارات اليدوية والآلية.', N'يستثنى من ذلك منتجات مثل الخلاطات والمسيلات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002012, N'غلايات (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها وعاء معدني مغطى لغلي الماء على الحرارة ، أو إبريق كهربائي لغلي الماء باستخدام عنصر مسخن داخلي. تشمل منتجات مثل إبريق غلايات المياه وغلايات "صفير" تقليدية.', N'يستثنى من ذلك منتجات مثل الجرار.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006739, N'أجهزة مزج المطبخ', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة خلط طعام تعمل بالطاقة مع ملحقات مختلفة يمكن استخدامها لخلط المكونات قبل الطهي. يمكن استخدام هذه المنتجات فقط للمزج.', N'يستثني الوظائف المتعددة لتقطيع المنتجات الغذائية وتقطيعها وخلطها. باستثناء المنتجات ذات الوظيفة الفردية مثل الخلاطات القائمة ، ومفرمات الطعام ، وتقطيع الطعام ، والسكاكين الكهربائية ، والمطاحن الكهربائية ، والمفارم ، بالإضافة إلى أي خلاطات وخلاطات غير تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006737, N'أجهزة تقطيع المطبخ', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة تقطيع طعام تعمل بالطاقة مع ملحقات مختلفة يمكن استخدامها لتقطيع المكونات قبل الطهي. يمكن استخدام هذه المنتجات فقط للتقطيع.', N'يستثني الوظائف المتعددة لتقطيع وخفق وخلط المنتجات الغذائية. باستثناء المنتجات ذات الوظيفة الفردية مثل الخلاطات القائمة ، والخلاطات اليدوية ، وتقطيع الطعام ، والسكاكين التي تعمل بالطاقة ، والمطاحن الكهربائية ، والمفارم ، بالإضافة إلى أي خلاطات وخلاطات غير تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006735, N'أجهزة خلط / مزج / تقطيع المطبخ', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة إعداد طعام تعمل بالطاقة مع ملحقات مختلفة يمكن استخدامها لأي عملية خلط أو تقطيع أو تقطيع أو خلط المكونات قبل الطهي. يمكن أن تقوم هذه المنتجات بالعديد من الوظائف المختلفة من التقطيع والتقطيع إلى شرائح والخفق والخلط. تشمل المنتجات تلك الأجهزة متعددة الوظائف.', N'باستثناء المنتجات ذات الوظيفة الفردية مثل الخلاطات القائمة ، والخلاطات اليدوية ، وقطاعة المطبخ ، ومفرامات الطعام ، والسكاكين التي تعمل بالكهرباء ، والمطاحن الكهربائية ، والمفارم ، بالإضافة إلى أي خلاطات وخلاطات غير تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006738, N'أجهزة خلط المطبخ', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها آلة خلط طعام تعمل بالطاقة مع ملحقات مختلفة يمكن استخدامها لخلط المكونات قبل الطهي. لا يمكن استخدام هذه المنتجات إلا للخلط.', N'يستثني الوظائف المتعددة لتقطيع وتقطيع وخلط المنتجات الغذائية. باستثناء المنتجات ذات الوظيفة الفردية مثل الخلاطات القائمة ، ومفرمات الطعام ، وتقطيع الطعام ، والسكاكين التي تعمل بالطاقة ، والمطاحن الكهربائية ، والمفارم ، بالإضافة إلى أي خلاطات وخلاطات غير تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006218, N'ماكينة صنع حليب الصويا / الأرز', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة كهربائية مصممة لصنع حليب المكسرات والفاصوليا والبذور والأرز الطازج.', N'باستثناء منتجات مثل صانعات الزبادي وآيس كريم.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002027, N'قطع غيار / ملحقات أجهزة العناية بالغسيل', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها قطع غيار / ملحقات لأجهزة العناية بالغسيل.', N'يستثني جميع منتجات الغسيل الكاملة الجاهزة للاستخدام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005317, N'مكيفات هواء - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز محمول للتحكم ، وخاصة لخفض درجة الحرارة والرطوبة في مكان مغلق.', N'باستثناء مكيفات الهواء الثابتة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005334, N'مبردات الهواء - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز محمول يبرد الهواء أو يحافظ على برودة الهواء.', N'يستثنى من ذلك مبردات الهواء الثابتة وكذلك وحدات تكييف الهواء المحمولة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005695, N'موازين المطبخ (بالطاقة)', N' يشمل أي منتجات يمكن وصفها / ملاحظتها كأداة تعمل بالطاقة أو آلة لوزن الطعام.', N'يستثنى على وجه التحديد موازين المطبخ التي لا تعمل بالكهرباء. باستثناء المنتجات مثل الموازين غير المصممة خصيصًا لوزن الطعام.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006736, N'أجهزة المطبخ', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كآلة تقطيع طعام تعمل بالطاقة مع ملحقات مختلفة يمكن استخدامها لتقطيع المكونات قبل الطهي. يمكن استخدام هذه المنتجات فقط للتقطيع.', N'يستثني الوظائف المتعددة لتقطيع وخفق وخلط المنتجات الغذائية. باستثناء المنتجات ذات الوظيفة الفردية مثل الخلاطات القائمة ، والخلاطات اليدوية ، وفرامات الطعام ، والسكاكين التي تعمل بالطاقة ، والمطاحن الكهربائية ، والمفارم ، بالإضافة إلى أي خلاطات وخلاطات غير تعمل بالطاقة.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002002, N'مسن السكاكين (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز كهربائي يتكون من فتحات يمكن من خلالها تمرير شفرة سكين لشحذها على قرص أو سطح داخلي ، مما يزيل الحافة القديمة لشحذ أخرى جديدة. يتضمن منتجات مثل المباري ذات 3 مراحل والمبرايات الماسية.', N'باستثناء منتجات مثل مسن السكاكين اليدوية والسكاكين الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001998, N'سكاكين (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز كهربائي يتكون من مقبض وشفرة متحركة تعمل بمحرك مخصص لتقطيع اللحوم أو غيرها من المواد الغذائية.', N'يستثنى من ذلك منتجات مثل السكاكين غير الكهربائية والمفرمات الكهربائية والمقطعات الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002004, N'مطاحن اللحوم / مفرمات (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز كهربائي يتكون عادة من صينية تغذية ، ومزلقة طعام وآلية مطحنة. تهدف هذه المنتجات إلى طحن اللحوم إلى قطع صغيرة ، على الرغم من إمكانية استخدام بعضها لطحن الأطعمة الأخرى مثل الخضار أو الجبن.', N'يستثنى من ذلك منتجات مثل المبشرات الكهربائية والمروحيات الكهربائية.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005868, N'نوافير مشروبات الحفلة (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها نافورة زخرفية للحفلات ، والتي تحتوي على مشروب للحفلات يتم ضخه من الوعاء السفلي إلى الأعلى حيث يتدفق إلى الأسفل في تأثير شلال من طبقة إلى أخرى. قد يحتوي الوعاء السفلي على أكواب مشروبات مرفقة ومن المرجح أن تكون مضاءة لتعزيز تأثير النافورة.', N'باستثناء نوافير الكولات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002003, N'سدادات الفراغ (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها أجهزة تعمل بالطاقة ، والتي تقوم بتوزيع الأكياس البلاستيكية وتقطيعها وإغلاقها بتفريغ الهواء بغرض الحفاظ على الطعام. قد تعمل هذه المنتجات تلقائيًا أو يمكن التحكم فيها من خلال تجاوز يدوي. تشمل منتجات مثل آلات تفريغ الهواء القائمة بذاتها وتحت المنضدة.', N'يستثنى من ذلك منتجات مثل أجهزة تفريغ الهواء غير المزودة بالطاقة وأكياس فراغ.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005691, N'فتاحات النبيذ / الزجاجات (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجهاز محمول باليد لفتح esay للنبيذ والزجاجات المماثلة. يمكن أن تكون هذه المنتجات سلكية أو لاسلكية ومتوفرة في مجموعة متنوعة من التصميمات. يتضمن منتجات مثل فتاحات زجاجات النبيذ التي تعمل بالطاقة المصممة هندسيًا من الفولاذ المقاوم للصدأ.', N'باستثناء منتجات مثل فتاحات العلب وفتاحات زجاجات النبيذ اليدوية ومسدسات ملفات تعريف الارتباط.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002014, N'ماكينات صنع الزبادي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز مصمم لتسخين خليط من الحليب والبكتيريا وتثبيته في درجة حرارة لفترة من الوقت ، حتى يتحول إلى زبادي.', N'باستثناء المنتجات مثل صانعات الزبادي المجمد وآيس كريم مزدوجة الوظائف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008120, N'أجزاء / ملحقات رفوف التجفيف والمطاحن', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجزء أو ملحقات من رف تجفيف أو مطحنة أو برج أو خط. تحتوي على منتجات مثل الأغطية الواقية وشاشات التجفيف والمثبتات الأرضية لتجفيف الأرفف ومجموعة متنوعة من الأنظمة لربط الملابس أو تعليقها.', N'باستثناء المنتجات مثل رفوف التجفيف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10008121, N'قطع غيار / ملحقات ألواح الكي', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كجزء أو ملحقات من لوح الكي. تحتوي على منتجات مثل أغطية لوح الكي ، الوقايات والبطانيات ، ألواح وأغطية الأكمام ، أدلة الأسلاك ، الوقايات الحديدية.', N'لا يشمل المنتجات مثل ألواح الكي.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002026, N'أجهزة العناية بالغسيل أخرى', N'يشمل أي منتجات قد يتم وصفها / ملاحظتها على أنها أحد منتجات أجهزة الغسيل ، حيث لا يستطيع مستخدم المخطط تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'باستثناء جميع منتجات أجهزة الغسيل المصنفة حاليًا.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10005335, N'أجهزة التحكم في الهواء - متعددة الوظائف - محمولة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كوحدة محمولة تجمع بين عمل العديد من الأجهزة مثل مكيف الهواء ومزيل الرطوبة والمروحة.', N'باستثناء أجهزة التحكم في الهواء الثابتة متعددة الوظائف.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006798, N'مزيل رطوبة الهواء - محمول (بدون كهرباء)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز محمول لتقليل الرطوبة.', N'يستثنى من ذلك مزيلات رطوبة الهواء الثابتة ومزيلات الرطوبة المحمولة التي تعمل بالطاقة.')
