INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10006894, N'أجهزة منزلية صغيرة - أخرى', N'يشمل أي منتجات قد يتم وصفها / ملاحظتها على أنها منتجات للأجهزة المنزلية الصغيرة ، حيث لا يتمكن مستخدم المخطط من تصنيف المنتجات في الطوب الموجود داخل المخطط.', N'')
