INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001985, N'صناع البيتزا', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة كهربائية مصممة خصيصًا لطهي البيتزا. عادةً ما يتكون المنتج من صينية قاعدة ساخنة يتم تمرير البيتزا عليها أو دعمها تحت عنصر تسخين. تشمل منتجات مثل صانعات البيتزا المفصلية بالكامل ذات الأغطية المغلقة وآلات صنع البيتزا مع عناصر تسخين متدلية جزئية.', N'يستثنى من ذلك منتجات مثل صانعات الخبز والأفران.')
