INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001984, N'صناع الفشار', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز كهربائي مصمم لتسخين حبات الذرة غير المفرومة حتى تنفجر وتنتفخ. قد تستخدم هذه المنتجات الهواء الساخن أو الدهون الساخنة لطهي الفشار. يشمل منتجات مثل آلات صنع الفشار المبتكرة / السينمائية.', N'يستثنى من ذلك منتجات مثل البوتاجازات.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001976, N'طناجر الضغط (تعمل بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها جهاز كهربائي مصمم لطهي الطعام عن طريق تسخين المياه ودفع البخار عبر الطعام. يشمل منتجات مثل الطباخات على شكل مقلاة وعلى شكل خزفي.', N'يستثنى من ذلك قدور الضغط التي لا تعمل بالطاقة والمستخدمة مباشرة على سطح الموقد وكذلك المنتجات مثل أجهزة الطهي البطيئة والأفران وأفران البخار.')
