INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001972, N'راكليت (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كأجهزة كهربائية مصممة لإذابة الجبن. يتكون هذا المنتج عادةً من قاعدة لحفظ الجبن ومصدر تسخين يتم تحته إذابة الجبن.', N'باستثناء المنتجات مثل مجموعات الفوندو.')
GO
INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001977, N'طباخات الأرز / طباخات بخار', N'يشمل أي منتجات يمكن وصفها / ملاحظتها كوحدة طهي مصممة خصيصًا لطهي الأرز من خلال استخدام البخار ، على الرغم من أن هذه المنتجات قد تكون مناسبة أيضًا لطهي الأطعمة الأخرى مثل الخضروات. يشمل منتجات مثل جهاز طهي الأرز بالميكروويف / أجهزة الطهي بالبخار وآلة طهي الأرز الكهربائية / أجهزة الطهي بالبخار.', N'يستثنى من ذلك المنتجات مثل قدور الطهي بالبخار وأوعية الطهي البطيئة وأوعية الضغط.')
