INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001974, N'المشواة / المحمصات (بالطاقة)', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها فرن غاز / كهربائي صغير توضع فيه اللحوم ، عادة الدواجن ، على صينية أو بصق دوار وتحميصها. تشمل منتجات مثل المشواة وأفران التحميص.', N'باستثناء منتجات مثل المشواة الغاز والأفران والميكروويف والشوايات الكهربائية.')
