INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10002034, N'أجهزة طهي صغيرة أخرى', N'يشمل أي منتجات قد يتم وصفها / ملاحظتها على أنها منتجات Cooking Appliance ، حيث يتعذر على مستخدم المخطط تصنيف المنتجات في قوالب موجودة داخل المخطط.', N'باستثناء جميع منتجات أجهزة الطهي المصنفة حاليًا')
