INSERT [dbo].[GPC] ([BrickCode], [BrickTitle], [BrickDefinition_Includes], [BrickDefinition_Excludes]) VALUES (10001968, N'أفران محمصة', N'يشمل أي منتجات يمكن وصفها / ملاحظتها على أنها فرن صغير قائم بذاته مصمم خصيصًا لتحميص منتجات الخبز. يمكن أيضًا استخدام هذه المنتجات لتدفئة منتجات أخرى', N'لا يشمل المنتجات مثل الأفران والشوايات والمحامص.')
